  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("p", null, "\uAE08\uC694\uC77C\uC778\uB370\uB3C4 \uBD88\uAD6C\uD558\uACE0, \uC624\uC804\uC744 \uACFC\uC81C\uD558\uB294 \uB370 \uBAA8\uB450 \uC37C\uB2E4. \uD55C\uAC00\uC9C0 \uACFC\uC81C\uB97C \uB193\uACE0 \uC0BC\uC77C \uB3D9\uC548 \uC6C5\uCF1C\uC7A1\uACE0 \uB05D\uC744 \uBABB\uB0B4\uB2E4\uAC00 \uC800\uB14112\uC2DC\uAE4C\uC9C0\uC758 \uB370\uB4DC\uB77C\uC778\uC774 \uB2E4\uAC00\uC624\uC790 \uC8FC\uC81C\uB97C \uD655\uC5F0\uD788 \uC7A1\uACE0 \uACFC\uC81C\uB97C \uD574\uB098\uAC14\uB2E4. \uAC70\uC758 3\uB144\uB9CC\uC758 \uD300\uBCC4\uACFC\uC81C\uC774\uB2E4 \uBCF4\uB2C8 \uB9CE\uC774 \uC5B4\uB835\uB2E4. \uACFC\uC81C\uC790\uCCB4\uBCF4\uB2E4 \uC0AC\uB78C\uB4E4\uAC04\uC758 \uC758\uACAC\uC77C\uCE58\uC5D0 \uC5B4\uB824\uC6C0\uC744 \uB290\uAF08\uB2E4\uACE0 \uD574\uC57C\uD558\uACA0\uB2E4. \uC0DD\uAC01\uC774\uB780 \uBAA8\uB450\uB4E4 \uB2E4\uB974\uB2C8 \uC8FC\uC81C\uC758 \uC120\uC815\uC5D0 \uC788\uC5B4\uC11C \uBCC0\uB860\uACFC \uBCC0\uC218\uAC00 \uB108\uBB34 \uB9CE\uB2E4. \uC774\uAC83\uC800\uAC83 \uC0DD\uAC01\uD558\uB2E4\uBCF4\uB2C8 \uD558\uB098\uC758 \uACB0\uD569\uC810\uC744 \uCC3E\uAE30 \uC5B4\uB824\uC6E0\uACE0, \uACB0\uAD6D\uC740 \uAC15\uB825\uD788 \uC8FC\uC7A5\uD558\uB294 \uC0AC\uB78C\uC758 \uC758\uACAC\uC744 \uB530\uB790\uB2E4.\n\uC790\uC720 \uC8FC\uC81C. \uD2B9\uC815\uD55C \uBAA9\uC801\uC774 \uC8FC\uC5B4\uC838 \uC788\uC5C8\uB2E4\uBA74 \uC624\uD788\uB824 \uAC04\uB2E8\uD588\uC73C\uB9AC\uB77C. \uC77C\uC815\uD55C \uBC94\uC704\uB97C \uC815\uD574\uB193\uACE0, \uADF8 \uC548\uC5D0\uC11C \uCD5C\uB300\uD55C \uCC3D\uC758\uB825\uC744 \uBC1C\uD718\uD558\uC5EC \uAC1C\uC131\uB118\uCE58\uB294 \uC8FC\uC81C\uB97C \uC120\uC815\uD574\uC57C\uD55C\uB2E4. \uADF8 \uBAA9\uD45C\uC810\uC758 \uC124\uC815\uC774 \uC65C\uADF8\uB807\uAC8C\uB098 \uC5B4\uB835\uB358\uC9C0. \uC624\uD788\uB824 \uB9CE\uC774 \uC544\uB294 \uAC83\uC774 \uB3C5\uC774 \uB420 \uC218 \uB3C4 \uC788\uB2E4. \uC8FC\uC81C\uB97C \uCC3E\uAE30 \uC704\uD574 \uC778\uD130\uB137\uC744 \uB3CC\uC544\uB2E4\uB2C8\uB2E4\uBCF4\uBA74 \uADF8 \uBC94\uC704\uC758 \uD655\uC7A5 \uB9CC\uD07C \uC6B0\uB9AC\uC758 \uCC3D\uC758\uC801 \uC2E4\uB825\uBC1C\uD718\uB3C4 \uC881\uD600\uC9C0\uAC8C \uB41C\uB2E4. \uC5B4\uB290 \uC815\uB3C4\uC758 \uBAA8\uBC29\uC740 \uCC3D\uC870\uC801 \uC0DD\uAC01\uC744 \uC704\uD55C \uC7AC\uB8CC\uAC00 \uB420 \uC218 \uC788\uC9C0\uB9CC, \uCC3D\uC758\uC801\uC778 \uC544\uC774\uB514\uC5B4\uB97C \uC704\uD55C \uCC3D\uC758\uC801 \uC544\uC774\uB514\uC5B4 \uCC3E\uAE30\uB294 \uC54C\uC544\uAC00\uB294 \uB9CC\uD07C \uCC3D\uC758\uC801 \uC0DD\uAC01\uC758 \uD55C\uACC4\uC5D0 \uB354 \uBE68\uB9AC \uBD80\uB52A\uD78C\uB2E4. \uACB0\uAD6D\uC740 \uBA87 \uAC00\uC9C0 \uC544\uC774\uB514\uC5B4\uB97C \uAC70\uCCE4\uC9C0\uB9CC \uAC00\uC7A5 \uCC98\uC74C \uC0DD\uAC01\uD55C \uC8FC\uC81C\uB97C \uC120\uD0DD\uD558\uAC8C \uB418\uC5C8\uB2E4. \uC0BC\uC77C\uAC04\uC758 \uC2DC\uAC04\uC744 \uCABC\uAC1C\uC5B4 \uAC00\uBA70 \uC4F0\uBA74\uC11C \uC758\uACAC\uC77C\uCE58\uB97C \uC704\uD55C \uC2DC\uAC04\uC774 \uC57D\uAC04\uC740 \uBB34\uC0C9\uD574\uC84C\uB2E4. \uADF8\uB798\uB3C4 \uD655\uC2E4\uD55C \uACB0\uC815\uC744 \uD1B5\uD574 \uC544\uCE68\uBD80\uD130 \uC81C\uC548\uC11C\uC640 \uD504\uB9AC\uC820\uD14C\uC774\uC158 \uBB38\uC11C\uB97C \uC791\uC131\uD558\uC5EC \uC624\uD6C4\uC5D0 \uB05D\uB0BC \uC218 \uC788\uC5C8\uB2E4. - \uACB0\uAD6D\uC740 \uC800\uB141 10\uC2DC\uAC00 \uB2E4\uB418\uC5B4 \uB05D\uB0C8\uC9C0\uB9CC."), mdx("p", null, "\uC624\uD6C4\uC5D4 \uC218\uC5C5\uC774 \uC788\uC5C8\uACE0, \uADF8 \uC218\uC5C5\uC758 \uAD50\uC218\uB2D8\uC758 \uCD94\uCC9C\uC73C\uB85C \uBAA8\uC758\uBA74\uC811\uC744 \uD558\uB294 \uAC83\uC744 \uCC38\uAD00\uD558\uB85C \uAC14\uB2E4. \uC138\uBBF8\uB098\uB97C \uB4E3\uAE30\uC704\uD574 50\uBD84\uAC04 \uBAA8\uC758\uBA74\uC811\uC744 \uBD24\uB294\uB370, \uADF8 \uC0AC\uC774\uC5D0 \uB290\uB080 \uAC83\uC740 \uC0C1\uB2F9\uD558\uB2E4."), mdx("hr", null), mdx("p", null, "4\uC6D43\uC77C\uB85C \uBD80\uD130 \uC77C\uC8FC\uC77C\uC774 \uC9C0\uB0AC\uB2E4. \uADF8 \uB3D9\uC548 \uC774\uAC83\uC800\uAC83 \uD560\uAC83\uC774 \uB9CE\uC544\uC11C \uAE00\uC744 \uB2E4 \uC4F0\uC9C0\uBABB\uD558\uB2E4\uAC00, \uC624\uB298 \uC218\uC5C5\uC774 \uC660\uC77C\uB85C \uBE68\uB9AC \uB05D\uB098\uC11C \uC2DC\uAC04\uC744 \uB0B4\uC5B4 \uB2E4\uC2DC \uAE30\uC5B5\uC744 \uB354\uB4EC\uC5B4 \uBCF4\uAE30\uB85C \uD558\uACA0\uB2E4."), mdx("p", null, "\uBAA8\uC758\uBA74\uC811. \uCC38\uAD00\uC5D0 \uC758\uBBF8\uB97C \uB450\uC5C8\uC9C0\uB9CC, \uB9C9\uC0C1 \uB2E8\uC0C1\uC704\uC5D0 \uC62C\uB77C \uBA74\uC811\uAD00\uB4E4\uC744 \uBC14\uB77C\uBCF4\uB294 \uB290\uB08C\uC740 \uC5B4\uB5A8\uAE4C? \uC55E\uC5D0 \uB098\uC11C\uC11C \uB9D0\uD558\uB294 \uAC83\uC870\uCC28\uB3C4 \uB5A8\uB9AC\uACE0 \uC5BC\uAD74\uC774 \uBD89\uC5B4\uC838 \uC628\uBAB8\uC774 \uB5A8\uB9BC\uC744 \uB290\uB07C\uB294 \uB098\uC5D0\uAC8C \uCC38\uAC00\uC778\uB4E4\uC740 \uC800 \uC138\uC0C1 \uB534\uB098\uB77C \uC0AC\uB78C\uB4E4\uCC98\uB7FC \uC0DD\uAC01\uC774 \uB418\uC5C8\uB2E4. \uAC70\uAE30\uC11C \uC11C\uC788\uB294\uAC83 \uC790\uCCB4\uAC00 \uB300\uB2E8\uD558\uACE0 \uBA4B\uC9C4\uB370, \uADF8\uB4E4\uC758 \uC0B6\uC758 \uB0B4\uC6A9\uC744 \uC0B4\uBA70\uC2DC \uB3CC\uC544\uBCF4\uBA74 \uB354\uC6B1\uB354 \uACBD\uD0C4\uD558\uAC8C \uB41C\uB2E4. \uC601\uC5B4\uB294 \uAE30\uBCF8, \uD1A0\uC775\uC740 \uC11C\uB958\uC2EC\uC0AC\uC5D0\uC11C \uC774\uBBF8 \uD55C\uACC4\uC9C0\uC5B4\uC838 \uAC78\uB7EC\uC9C0\uACE0, \uAC01\uC885 \uB3D9\uC544\uB9AC \uD65C\uB3D9\uC5D0\uC11C\uC758 \uADF8\uB4E4\uC758 \uD65C\uC57D\uC740 \uC5BC\uB9C8\uB098 \uB300\uB2E8\uD558\uB358\uC9C0. \uC81C2\uC678\uAD6D\uC5B4 \uAD6C\uC0AC\uC5D0\uB2E4\uAC00 \uD574\uC678\uC5EC\uD589\uACFC \uC720\uD559\uC740 \uAE30\uBCF8\uC774\uB2E4. \uC790\uC2E0\uC758 \uBD84\uC57C\uC758 \uAD00\uC2EC\uC774 \uC788\uACE0 \uB9CE\uC740 \uBD80\uBD84\uC744 \uC54C\uACE0 \uC788\uB294 \uAC83\uC740 \uAE30\uBCF8 \uC911\uC5D0 \uAE30\uBCF8\uC774\uB2E4. \uAE54\uB054\uD55C \uC815\uC7A5\uACFC \uB2E8\uC815\uD55C \uBA38\uB9AC, \uBC14\uB978 \uC790\uC138\uC5D0\uC11C \uB098\uC624\uB294 \uCC28\uBD84\uD55C \uBAA9\uC18C\uB9AC. \uADF8\uB9AC\uACE0 \uAC00\uC7A5 \uB208\uC5D0 \uB744\uB294 \uAC83\uC740 \uADF8\uB4E4\uC758 \uB9D0\uC19C\uC528\uB2E4. \uB9D0\uC744 \uC798 \uD560\uB9CC\uD07C \uB2E4\uBD84\uC57C\uC758 \uACBD\uD5D8\uACFC \uC790\uC2E0\uC758 \uBD84\uC57C\uC5D0 \uB300\uD574 \uC900\uBE44\uD574\uC628 \uBAA8\uC2B5\uC774 \uB208 \uC55E\uC5D0 \uC120\uD558\uB2E4. \uB2E4\uBD84\uD788 \uC77C\uBC18\uC801\uC778 \uBA74\uC811\uC778\uB4E4\uC758 \uC2A4\uD399\uC774\uB77C\uB2C8\u2026"), mdx("p", null, "\uB2E4\uB978 \uC0AC\uB78C\uACFC\uC758 \uBE44\uAD50\uAC00 \uC790\uC2E0\uC758 \uC758\uC2DD\uC744 \uC57D\uD558\uAC8C \uB9CC\uB4DC\uB294 \uAC83\uC744 \uC54C\uAE30 \uB54C\uBB38\uC5D0, \uADF8\uB7EC\uD55C \uC758\uC2DD \uC790\uCCB4\uB97C \uC548\uD558\uAE30 \uC704\uD574, \u2018\uD2B9\uBCC4\uD55C\u2019 \uC0AC\uB78C\uB4E4\uC774\uB77C\uACE0 \uC77C\uB2E8\uB77D \uC9C0\uC73C\uBA74 \uD3B8\uD558\uC9C0\uB9CC, 1\uB144 \uB4A4\uC5D0 \uB098\uC758 \uC0DD\uD65C \uC18D\uC5D0 \uD68C\uC0AC\uC640 \uBA74\uC811\uC774\uB77C\uB294 \uAC83\uC774 \uD3BC\uCCD0\uC9C8 \uAC83\uC774\uB77C\uB294 \uAC83\uC744 \uC54C\uACE0 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uC544\uBB34 \uC0DD\uAC01\uC5C6\uC774 \uB118\uC5B4\uAC00\uAE30\uC5D4 \uB108\uBB34\uB098\uB3C4 \uBB34\uB9AC\uAC00 \uC788\uB294 \uAC83 \uAC19\uB2E4.\n\uB0B4\uB144\uC5D0 \uB098\uB3C4 \uC800\uC815\uB3C4\uCBE4\uC774 \uB420 \uC218 \uC788\uACA0\uC9C0\uB77C\uB294 \uADFC\uAC70\uC5C6\uB294 \uBB34\uC2DD\uD55C \uC790\uC2E0\uAC10\uC774 \uC788\uAE30\uC5D0 \uC544\uC9C1\uC740 \uAC71\uC815\uC774 \uC548\uB418\uC9C0\uB9CC \uB9C9\uC0C1 \uC0C1\uD669\uC774 \uB2E5\uCCE4\uC744 \uB54C \uC774\uC0C1\uACFC \uC2E4\uC7AC\uC758 \uBAA8\uC2B5\uC774 \uB2E4\uB978 \uAC83\uC5D0 \uB300\uD55C \uAC71\uC815\uC774 \uC5C6\uB294 \uAC83\uC740 \uC544\uB2C8\uB2E4. \uBB50 \uC5B4\uCC0C\uB410\uB358 \uAC04\uC5D0, \uADF8\uB7EC\uD55C \uBA74\uC811\uCC38\uAD00\uC744 \uD1B5\uD574\uC11C \uB290\uB080 \uAC83\uC740 \uC0AC\uD68C \uD604\uC2E4\uC5D0 \uB300\uD55C \uC870\uAE08\uC758 \uBD80\uC815\uC774\uB2E4.\n\uC0AC\uD68C \uC790\uCCB4\uAC00 \uBB34\uD55C \uACBD\uC7C1\uC2DC\uB300\uB85C \uB3CC\uC785\uD588\uACE0 \uB354\uAD70\uB2E4\uB098 \uACBD\uC81C\uCE68\uCCB4\uAC00 \uAC00\uBBF8\uD574\uC8FC\uC5B4\uC11C \uC0AC\uB78C\uB4E4\uC758 \uACBD\uC7C1\uC774 \uB354\uC6B1\uB354 \uCE58\uC5F4\uD574\uC84C\uB2E4\uB294 \uAC83\uC740 \uB204\uAD6C\uB098 \uB290\uB07C\uACE0 \uC788\uC744 \uAC83\uC774\uB2E4. \uADF8\uB9CC\uD07C \uC0AC\uB78C\uB4E4\uC758 \uC778\uC0DD\uC5D0 \uB300\uD55C \uC0DD\uAC01\uC790\uCCB4\uB3C4 \uB9CE\uC774 \uBCC0\uD588\uACE0, \u2018\uAE30\uBCF8\u2019\uC774\uB77C\uB294 \uAE30\uC900\uC774 \uB108\uBB34\uB098\uB3C4 \uB192\uC544\uC84C\uB2E4. \uAE30\uBCF8, \uAE30\uBCF8, \uAE30\uBCF8,,, \uC774 \uC815\uB3C4\uB294 \uAE30\uBCF8\uC544\uB2C8\uC5EC? \uB77C\uACE0 \uB2F9\uC5F0\uD788 \uB9D0\uD558\uB294 \uAC83. \uADF8\uB9AC\uACE0 \uC6B0\uB9AC\uB294 \uC5B4\uCA54 \uC218 \uC5C6\uC774 \uB192\uC544\uC9C4 \uAE30\uBCF8\uC774\uB77C\uB294 \uAE30\uC900\uC5D0 \uB9DE\uCD94\uC5B4 \uC778\uC0DD\uC744 \uC0B4\uC544\uAC00\uC57C\uD55C\uB2E4\uB294 \uAC83. \uC0AC\uD68C\uC758 \uC694\uAD6C\uC5D0 \uB9DE\uB294 \uC5ED\uD560\uC744 \uD574\uC904\uB54C\uC57C \uC6B0\uB9AC\uB294 \uD68C\uC0AC\uC778, \uC0AC\uD68C\uC778\uC73C\uB85C\uC368 \uB300\uC811\uC744 \uBC1B\uC744 \uC218 \uC788\uB294\uC9C0\uB77C \uBB34\uC2DC\uD574\uBC84\uB9B4 \uC218\uB3C4 \uC5C6\uB294 \uB178\uB987\uC774\uB2E4. \uC774\uB7EC\uC9C0\uB3C4 \uBABB\uD558\uACE0 \uC800\uB7EC\uC9C0\uB3C4 \uBABB\uD558\uACE0 \uB530\uB77C\uAC08 \uC218 \uBC16\uC5D0 \uC5C6\uB294 \uAC83\uC774 \uD604\uC2E4\uC778\uAC00?\n\uAE30\uBCF8\uC5D0 \uB300\uD55C \uAE30\uC900. \uAE30\uC900\uC758 \uBCC0\uD55C\uB9CC\uD07C \uC778\uC0DD\uC758 \uB09C\uC774\uB3C4 \uC790\uCCB4\uAC00 \uC608\uC804\uC5D0 \uBE44\uD574 \uB9CE\uC774 \uB192\uC544\uC9C4 \uAC83 \uC544\uB2D0\uAE4C?"), mdx("p", null, "\uD1A0\uC775\uC601\uC5B4\uD574\uC678\uC5EC\uD589\uC720\uD559\uC5B4\uD559\uC5F0\uC218\uC804\uBB38\uC9C0\uC2DD\uD559\uC810\uC790\uAE30\uACC4\uBC1C\uB300\uC778\uC778\uAC04\uAD00\uACC4\uB9D0\uBAA9\uC18C\uB9AC\uC790\uC2E0\uAC10\uC5F4\uC815\uB178\uB825\uB048\uAE30\uC778\uB0B4\uC637\uCC28\uB9BC\uBA4B\uC788\uC74C\uC5BC\uAD74\uD53C\uBD80\uAD00\uB9AC\uC6B4\uB3D9\uAC74\uAC15\uD5