    mdxType: "MDXLayout"
  }), mdx("p", null, "[011]"), mdx("h2", null, "\uC11C\uC5F0\uC774 \uB3CC\uC794\uCE58\uC5D0 \uAC00\uB294 \uAE38\uC5D0"), mdx("p", null, "\uAE00\uC744 \uC4F4\uB2E4. \uC870\uCE74 \uC11C\uC5F0\uC774\uC758 \uB3CC\uC794\uCE58\uC5D0 \uAC04\uB2E4. \uBB54\uAC00 \uBAA8\uB97C \uD53C\uACE4\uD568\uC774 \uC788\uB2E4. \uC5B4\uC81C \uBC24 \uB2A6\uAC8C \uAE4C\uC9C0 \uAC8C\uC784\uC744 \uD574\uC11C \uADF8\uB7F0\uAC00? \uC694\uC0C8 \uAC00\uB054\uC529 \uBBF8\uCE5C\uB4EF\uC774 \uAC8C\uC784\uC744 \uD560 \uB54C\uAC00 \uC788\uB2E4. \uD68C\uC0AC\uC758 \uC77C\uC744 \uCC98\uB9AC\uD558\uB290\uB77C \uC815\uC2E0\uC774 \uC5C6\uC5B4 \uC2DC\uAC04\uC774 \uBD80\uC871\uD55C\uB300, \uC9E7\uC740 \uC2DC\uAC04\uC5D0 \uC27D\uAC8C \uC7AC\uBBF8\uB97C \uBCFC \uC218 \uC788\uB294 \uBC29\uBC95\uC774\uAE30 \uB54C\uBB38\uC774\uB2E4. \uC775\uC219\uD558\uAE30\uB3C4 \uD558\uACE0, \uBB54\uAC00 \uBAA8\uB97C \uCF8C\uAC10\uC744 \uB290\uB084 \uC218 \uC788\uB294 \uB180\uC774 - \uAC8C\uC784. \uAC00\uB054\uC529 \uACFC\uD558\uAC8C \uD574\uC11C, \uBAB8\uACFC \uB9C8\uC74C\uC774 \uD53C\uACE4\uD574\uC9C0\uAE30\uB3C4 \uD558\uC9C0\uB9CC, \uAC00\uB054\uC529 \uC8FC\uB9D0\uC5D0 \uBB34\uC5C7\uC744 \uD574\uC57C\uD560\uC9C0 \uBAA8\uB974\uB294 \uADF8\uB7F0 \uC2DC\uAC04\uC774 \uC624\uBA74, \uCE5C\uAD6C\uC640 \uAC19\uC774 \uAC8C\uC784\uC744 \uD558\uACE4 \uD55C\uB2E4."), mdx("p", null, "\uC5B4\uC81C \uC11C\uC6B8\uC5D0\uC11C \uB3CC\uC544\uC654\uB2E4. \uC774\uBC88\uC8FC \uC77C\uC815\uC740 \uB2E4\uB978 \uC8FC\uC640 \uB2E4\uB974\uAC8C \uBE61\uBE61\uD558\uB2E4."), mdx("h2", null, "\uC11C\uC6B8\uC5D0\uC11C\uC758 \uBAA8\uC2B5\uB4E4 - \uCC1C\uC9C8\uBC29?"), mdx("p", null, "\uC774\uBC88\uC5D0 \uD22C\uC790\uC790\uAC00 \uC11C\uC6B8 \uC0AC\uBB34\uC2E4\uB85C \uC628\uB2E4\uD558\uC5EC, \uC778\uC6D0\uC218\uB97C \uCC44\uC6CC \uC6B4\uC601\uC744 \uC798 \uD558\uACE0 \uC788\uB2E4\uB294 \uBAA8\uC2B5\uC744 \uBCF4\uC5EC\uC904 \uC0DD\uAC01\uC73C\uB85C, \uBD80\uC0B0\uD300\uC774 \uC11C\uC6B8\uB85C \uAC14\uC5C8\uB2E4. \uC11C\uC6B8\uC5D0 \uAC00\uBA74 \uBA87 \uC77C\uC529 \uC788\uB2E4\uAC00 \uB0B4\uB824\uC624\uB294\uB370, \uC7A0\uC790\uB9AC\uAC00 \uBB38\uC81C\uAC00 \uB41C\uB2E4. \uCE5C\uAD6C\uB4E4\uC5D0\uAC8C \uBD80\uD0C1\uC744 \uD574\uB3C4 \uB418\uC9C0\uB9CC, \uC660\uC9C0 \uBAA8\uB974\uAC8C \uBBFC\uD3D0\uB97C \uB07C\uCE58\uB294 \uAC83 \uAC19\uC544\uC11C \uD558\uB8E8\uBC24 \uC81C\uC6CC\uB2EC\uB77C\uACE0 \uB9D0 \uD558\uC9C8 \uC54A\uB294\uB2E4. \uADF8 \uC815\uB3C4\uB85C \uD3B8\uD55C \uCE5C\uAD6C\uAC00 \uC5C6\uC5B4\uC11C\uC774\uAE30\uB3C4 \uD558\uACA0\uB124. \uADF8\uB798\uC11C \uCC1C\uC9C8\uBC29\uC5D0 \uC794\uB2E4."), mdx("p", null, "\uC774\uC804\uC5D0 \uC11C\uC6B8\uC5D0 \uC62C\uB77C\uC654\uC744 \uB54C\uC758 \uC77C\uC774\uB2E4. \uC5B8\uC81C \uB0B4\uB824\uAC08\uC9C0 \uBAA8\uB974\uB294 \uAE30\uC57D \uC5C6\uB294 \uC77C\uC815 \uB54C\uBB38\uC5D0 \uC815\uC2E0\uC801\uC778 \uC2A4\uD2B8\uB808\uC2A4\uB97C \uBC1B\uACE0 \uC788\uC5C8\uB2E4. \uBAB8\uC740 \uAD1C\uCC2E\uC740\uB370, \uBB34\uC5C7\uC778\uAC00 \uBAA8\uB97C \uC815\uC2E0\uC801\uC778 \uD5C8\uC804\uD568\uC774 \uB290\uAEF4\uC84C\uC5C8\uB2E4. \uC774\uB7F0 \uAC83\uC774 \uC678\uB85C\uC6C0\uC774\uB77C\uB294 \uAC10\uC815\uC778\uAC00? \uC774 \uB54C\uC758 \uBB54\uAC00 \uBAA8\uB97C \uD5C8\uC804\uD568\uC744 \uCC44\uC6CC\uC904 \uD558\uB098\uC758 \uBC29\uBC95\uC774 \uC788\uB294\uB370, \uBC14\uB85C \uC0C1\uD6C8\uC774\uC640 \uAC19\uC774 \uAC8C\uC784\uC744 \uD558\uB294 \uAC83\uC774\uB2E4. \uC0C1\uD6C8\uC774\uAC00 \uC11C\uC6B8\uC5D0 \uD63C\uC790 \uC0B4 \uB54C, \uBC24\uB9C8\uB2E4 \uC804\uD654\uB97C \uD574\uC11C \uAC8C\uC784\uC744 \uAC19\uC774 \uD558\uC790\uACE0 \uD588\uB358 \uADF8 \uB9C8\uC74C\uC744 \uC870\uAE08\uC740 \uC54C \uAC83 \uAC19\uB2E4. 1~2\uB144\uC758 \uAE34 \uC2DC\uAC04\uB3D9\uC548 \uD640\uB85C \uC790\uCDE8\uBC29\uC5D0\uC11C \uD63C\uC790 \uBA39\uACE0 \uC790\uACE0 \uD588\uC744 \uCE5C\uAD6C\uB97C \uBCF4\uBA74 \uCC38 \uB300\uB2E8\uD558\uB2E4. \uB0B4\uAC00 \uC11C\uC6B8\uC5D0\uC11C \uADF8\uB807\uAC8C \uC0B4\uACE0 \uC788\uB2E4\uBA74, \uB09C \uC5B4\uB5A4 \uAC10\uC815\uB4E4\uC744 \uB290\uB07C\uACE0 \uC788\uC744\uAE4C? \uADF8 \uC0C1\uD669\uC5D0 \uB9DE\uCDB0 \uB098\uB984\uB300\uB85C \uC801\uC751\uC744 \uD574\uB098\uAC00\uAE34 \uD558\uACA0\uC9C0\uB9CC, \uC660\uC9C0 \uBAA8\uB97C \uD5C8\uC804\uD568\uC774 \uBD84\uBA85\uD788 \uC788\uACA0\uC9C0. \uADF8 \uC678\uB85C\uC6C0\uACFC \uD5C8\uC804\uD568\uC744 \uCC44\uC6B0\uAE30 \uC704\uD55C \uBC29\uBC95\uC744 \uCC3E\uC544 \uB098\uC11C\uACA0\uC9C0. \uD63C\uC790\uC11C 4\uC77C\uB3D9\uC548 \uCC1C\uC9C8\uBC29\uC5D0\uC11C \uC7A4\uB2E4. \uC5B4\uB290 \uC815\uB3C4 \uC801\uC751\uB3C4 \uD588\uB2E4. \uC775\uC219\uD558\uB2E4. \uC774\uC81C\uB294 \uADF8\uB0E5 \uADF8\uB7EC\uB824\uB2C8 \uD55C\uB2E4."), mdx("h2", null, "\uAC19\uC740 \uACF3\uC774\uB77C\uB3C4 \uB54C\uC5D0 \uB530\uB77C \uB290\uB08C\uC774 \uB2E4\uB974\uB2E4."), mdx("p", null, "\uAC19\uC740 \uCC1C\uC9C8\uBC29\uC774\uB77C\uB3C4 \uBB34\uC2A8 \uC774\uC720\uB85C, \uB204\uAD6C\uC640 \uAC00\uB290\uB0D0\uC5D0 \uB530\uB77C \uB290\uB08C\uC774 \uB2E4\uB974\uB2E4. \uC608\uC804 \uBD80\uC0B0\uC5D0\uC11C \uC6B8\uC9C4\uAE4C\uC9C0 \uAC78\uC5B4\uC11C \uB3C4\uBCF4 \uC5EC\uD589\uC744 \uD55C\uC801\uC774 \uC788\uB294\uB370, \uADF8\uB54C\uB294 \uAC70\uC758 \uC5F4\uD758 \uAC00\uB7C9 \uCC1C\uC9C8\uBC29\uC5D0\uC11C \uBCF4\uB0C8\uB2E4. \uADF8\uB54C\uB294 \uADF8 \uACF5\uAC04\uC774 \uD558\uB098\uC758 \uD734\uC2DD\uC758 \uACF5\uAC04\uC774\uC5C8\uB2E4. \uD53C\uACE4\uD574\uC9C4 \uB2E4\uB9AC\uB294 \uD480\uACE0, \uC544\uB291\uD788 \uC274 \uC218 \uC788\uB294 \uACF3. \uCC1C\uC9C8\uBC29\uC774 \uC88B\uAC70\uB098, \uD5C8\uB984\uD558\uAC70\uB098 \uD558\uB294 \uBB38\uC81C\uB294 \uC911\uC694\uD558\uC9C0 \uC54A\uC558\uB2E4. \uD558\uB8E8\uC758 \uB3C4\uCC29\uC9C0\uC600\uACE0, \uC9D1\uACFC \uAC19\uC740 \uACF5\uAC04\uC774\uC5C8\uAE30 \uB54C\uBB38\uC5D0 \uC628\uC804\uD788 \uC274 \uC218 \uC788\uB294 \uACF5\uAC04\uC774\uC5C8\uB2E4. \uBE68\uB798\uB3C4 \uD558\uACE0, \uC5EC\uD589 \uC77C\uC815\uB3C4 \uC9DC\uACE0, \uD558\uB8E8\uB97C \uC815\uB9AC\uD558\uBA70 \uBA54\uBAA8\uB97C \uC4F0\uAE30\uB3C4 \uD588\uB2E4."), mdx("p", null, "\uADF8\uB807\uC9C0\uB9CC \uC774\uBC88\uC5D0 \uC11C\uC6B8\uC5D0\uC11C \uC794 \uACF5\uAC04\uC740 \uB2E8\uC9C0 \uC7A0\uC744 \uC704\uD55C \uACF3\uC774\uC5C8\uB2E4. \uBC24 \uB2A6\uAC8C \uAE4C\uC9C0 \uC77C\uC744 \uD558\uACE0, \uC7A0\uAE50 \uB208\uC744 \uBD80\uCE60 \uC218 \uC788\uB294 \uACF3. \uC77C\uC5B4\uB098\uBA74 \uB2E4\uC2DC \uC53B\uACE0 \uC0AC\uBB34\uC2E4\uB85C \uD5A5\uD55C\uB2E4. \uB530\uB73B\uD55C \uC628\uD0D5\uC5D0\uC11C \uD479 \uC26C\uAE30\uB3C4 \uD558\uC9C0\uB9CC, \uBAB8\uACFC \uB9C8\uC74C\uC740 \uC644\uC804\uD788 \uD68C\uBCF5\uB418\uC9C4 \uC54A\uB294\uB2E4. \uC57D\uAC04 \uD53C\uACE4\uD574\uC838 \uC788\uB294 \uC0C1\uD0DC\uC774\uB2E4."), mdx("p", null, "\uACB0\uAD6D\uC740 \uCC1C\uC9C8\uBC29\uC5D0 \uD558\uB294 \uAC83\uB4E4\uC740 \uAC19\uB2E4. \uC53B\uACE0, \uC790\uACE0, \uB2E4\uC2DC \uC53B\uACE0 \uB098\uC624\uACE0. \uADF8\uB807\uC9C0\uB9CC \uB290\uB08C\uC740 \uB9CE\uC774 \uB2E4\uB974\uB2E4. \uC628\uC804\uD788 \uC274 \uC218 \uC788\uB294 \uACF5\uAC04 vs \uC7A0\uC744 \uC790\uC57C\uB9CC \uD558\uB294 \uACF5\uAC04"), mdx("p", null, "\uADF8\uB798\uB3C4 \uC11C\uC6B8 \uC77C\uC815\uB3C4 \uB0B4\uAC00 \uD558\uACE0\uC790 \uD558\uB294 \uAC83\uC744 \uC704\uD574 \uAC10\uC218\uD558\uB294 \uAC83\uB4E4 \uC911 \uD558\uB098\uC774\uB2E4. \uCC1C\uC9C8\uBC29\uC5D0\uC11C \uD640\uB85C \uC790\uB294 \uAC83\uB3C4 \uAE0D\uC815\uC801\uC73C\uB85C \uC0DD\uAC01\uD55C\uB2E4. \uB098\uC758 \uC815\uC2E0\uBCF4\uD638\uB97C \uC704\uD574\uC11C\uB3C4 \uADF8\uAC8C \uC88B\uB2E4. \uC774\uBC88\uC5D0\uB294 \uC9E7\uC740 \uC77C\uC815\uC73C\uB85C \uB0B4\uB824\uC624\uAE34 \uD588\uC9C0\uB9CC, \uB9CC\uC57D \uC815\uB9D0\uB85C \uC11C\uC6B8\uB85C \uC774\uC0AC\uB97C \uD558\uAC8C \uB418\uC5B4 \uC0DD\uD65C\uD558\uAC8C \uB41C\uB2E4\uBA74\u2026?"), mdx("p", null, "\uB098\uB984\uB300\uB85C \uC801\uC751\uD558\uAC8C \uB418\uACA0\uC9C0\n\uC4F8\uC4F8\uD558\uBA74 \uC4F8\uC4F8\uD55C\uB300\uB85C, \uC678\uB85C\uC6B0\uBA74 \uC678\uB85C\uC6B4\uB300\uB85C."));
