  }), mdx("h1", null, "\uC624\uB9AC\uC9C0\uB110\uC2A4 - \uC560\uB364 \uADF8\uB79C\uD2B8"), mdx("p", null, "\uC2A4\uD0C0\uD2B8\uC5C5\uC744 \uC2DC\uC791\uD55C\uC9C0 9\uB144\uC774 \uB418\uC5C8\uB2E4. \uB69C\uB837\uD55C \uC131\uACFC\uB97C \uB0B4\uC9C0 \uBABB\uD574 \uC804\uC804\uAE0D\uAE0D\uD558\uBA70 \uC9C0\uB0B4\uACE0 \uC788\uB2E4. \uB0B4\uAC00 \uCC3D\uC5C5\uD558\uACE0\uB294 \uAC70\uB9AC\uAC00 \uBA3C \uC0AC\uB78C\uC774\uC5C8\uB358 \uAC83\uC77C\uAE4C? \uC9C0\uAE08\uC640\uC11C \uC0DD\uAC01\uD574\uBCF4\uBA74 \uB108\uBB34 \uBA4D\uCCAD\uD558\uAC8C \uC0AC\uC5C5\uC744 \uD558\uACE0 \uC788\uB2E4\uB294 \uC0DD\uAC01\uC774 \uB4E4\uC5C8\uB2E4. \uD558\uB8E8\uD558\uB8E8 \uCC98\uB9AC\uD574\uC57C\uD560 \uC77C\uC5D0 \uC774\uB9AC\uC800\uB9AC \uCE58\uC5EC\uC11C, \uB3C5\uCC3D\uC801\uC774\uACE0 \uD06C\uB808\uC774\uD2F0\uBE0C\uD55C \uC77C\uB4E4\uC744 \uB193\uCE58\uACE0 \uC788\uC5C8\uAE30 \uB54C\uBB38\uC774\uB2E4. \uADF8\uB798\uC11C \uC774\uB97C \uAC1C\uC120\uD574\uBCF4\uACE0\uC790 \u2018\uC624\uB9AC\uC9C0\uB110\uC2A4\u2019\uB77C\uB294 \uCC45\uC744 \uC77D\uAC8C \uB418\uC5C8\uB2E4."), mdx("p", null, "\uCC45\uC744 \uC77D\uC73C\uBA74\uC11C \uAC00\uC7A5 \uB9C8\uC74C\uC5D0 \uC640\uB2FF\uC558\uB358 \uAC83\uC740 \u2018\uC544\uC774\uB514\uC5B4\uC758 \uC218\uC758 \uC911\uC694\uC131\u2019\uC774\uB2E4. \uC5EC\uB7EC\uAC00\uC9C0\uB97C \uC2DC\uB3C4\uD558\uB294 \uAC83\uC774 \uD558\uB098\uB97C \uBB3C\uACE0\uB298\uC5B4\uC9C0\uB294 \uAC83\uBCF4\uB2E4 \uB354 \uC88B\uC744 \uC218 \uC788\uB2E4\uB294 \uAC83\uC774\uB2E4. \uAD50\uC218\uAC00 \uD559\uC0DD\uB4E4\uC5D0\uAC8C \uC0AC\uC9C4\uC744 \uCC0D\uB294 \uACFC\uC81C\uB97C \uAC01\uAC01 \uB0B4\uC5B4\uC8FC\uC5C8\uB2E4. \uD558\uB098\uB97C \uC815\uB9D0 \uC798 \uCC0D\uC5B4\uC11C \uC81C\uCD9C\uD558\uB294 \uAC83 \uADF8\uB8F9\uACFC \uB2E4\uC591\uD55C \uC0AC\uC9C4\uC744 \uCC0D\uC5B4\uC11C \uC81C\uCD9C\uD558\uB294 \uADF8\uB8F9\uC744 \uBE44\uAD50\uD558\uC600\uB2E4. \uC815\uB9D0 \uC88B\uC740 \uC791\uD488\uC740 \uB2E4\uC591\uD55C \uC0AC\uC9C4\uC744 \uCC0D\uC5B4\uC11C \uB0B8 \uADF8\uB8F9\uCABD\uC5D0\uC11C \uB354 \uB9CE\uC774 \uB098\uC654\uB2E4. \uC9C0\uAE08 \uD558\uACE0 \uC788\uB294 \uC77C\uC5D0\uC11C\uB3C4 \uC774\uB97C \uC2E4\uCC9C\uD574\uBD10\uC57C\uACA0\uB2E4. \uC5EC\uB7EC \uC544\uC774\uB514\uC5B4\uB97C \uC0DD\uAC01\uD558\uACE0 \uC9C1\uC811 \uC2E4\uCC9C\uD574\uBCF4\uB294 \uAC83\uC774 \uC131\uACF5\uC73C\uB85C \uAC00\uB294 \uC9C0\uB984\uAE38\uC77C \uAC83\uC774\uB2E4."), mdx("p", null, "\uADF8\uB9AC\uACE0 \uC704\uD5D8 \uD3EC\uD2B8\uD3F4\uB9AC\uC624\uC758 \uADE0\uD615\uC744 \uC720\uC9C0\uD558\uB294 \uAC83\uC774 \uC911\uC694\uD558\uB2E4\uB294 \uAC83\uB3C4 \uC54C\uAC8C \uB418\uC5C8\uB2E4. \uD604\uC7AC \uB098\uC758 \uC0C1\uD669\uC5D0\uC11C \uC9C1\uC811\uC801\uC73C\uB85C \uB290\uB07C\uACE0 \uC788\uB294 \uAC83\uC774\uAE30\uC5D0 \uC774\uD574\uAC00 \uC798\uB418\uC5C8\uB2E4. \uD55C \uACF3\uC5D0 \uC62C\uC778\uC744 \uD558\uB2E4\uBCF4\uB2C8 \uACBD\uC81C\uC801\uC73C\uB85C\uB3C4 \uC5B4\uB824\uC6C0\uC744 \uACAA\uAC8C \uB418\uC5C8\uB2E4. \uC774\uB807\uAC8C \uB418\uB2E4\uBCF4\uB2C8 \uBCF8\uC5C5\uB3C4 \uC9C0\uC7A5\uC744 \uBC1B\uAC8C \uB418\uC5C8\uB2E4. \uC0B6\uC758 \uADE0\uD615\uC5D0 \uB300\uD574 \uB354\uC6B1 \uC0DD\uAC01\uC744 \uD574\uBD10\uC57C\uACA0\uB2E4."), mdx("p", null, "\uBCC0\uD654\uB97C \uAC00\uC838\uC624\uB294 \uD558\uB294 \uBC95, \uCC3D\uC758\uC801\uC774\uB77C\uB294 \uAC83\uC5D0 \uB300\uD574 \uC5B4\uB5A4 \uC758\uBBF8\uB97C \uAC00\uC9C4 \uAC83\uC778\uC9C0 \uC0DD\uAC01\uD574\uBCFC \uC218 \uC788\uB294 \uC2DC\uAC04\uC774\uC5C8\uB2E4. ---"), mdx("hr", null), mdx("h1", null, "\uAC1C\uC778\uC744 \uC704\uD55C \uD589\uB3D9 \uC81C\uC548"), mdx("h2", null, "\uB3C5\uCC29\uC815\uC778 \uC544\uC774\uB514\uC5B4\uB97C \uCC3D\uCD9C\uD558\uACE0 \uC2DD\uBCC4\uD558\uAE30"), mdx("h3", null, "\uAE30\uC874\uC758 \uCCB4\uC81C\uC5D0 \uC758\uBB38\uC744 \uB358\uC838\uB77C."), mdx("p", null, "\uC560\uCD08\uC5D0 \uC65C \uADF8\uB7F0 \uC0C1\uD0DC\uAC00 \uC874\uC7AC\uD558\uAC8C \uB418\uC5C8\uB294\uC9C0 \uC758\uBB38\uD558\uB77C. \uACE0\uC815 \uBD88\uBA74\uC774 \uC544\uB2C8\uB77C\uB294 \uC0AC\uC2E4\uC774 \uBD84\uBA85\uD574\uC9C4\uB2E4. \uADF8\uB807\uAC8C \uB418\uBA74 \uC5B4\uB5BB\uAC8C \uAC1C\uC120\uD560\uC9C0 \uC0DD\uAC01\uD558\uAC8C \uB41C\uB2E4."), mdx("h3", null, "\uC790\uC2E0\uC774 \uCC3D\uCD9C\uD558\uB294 \uC544\uC774\uB514\uC5B4\uC758 \uC218\uB97C \uC138 \uBC30\uB85C \uB298\uB824\uB77C."), mdx("p", null, "\uB3C5\uCC3D\uC131\uC744 \uB192\uC774\uB294 \uAC00\uC7A5 \uC26C\uC6B4 \uBC29\uBC95\uC740 \uC544\uC774\uB514\uC5B4\uB97C \uB9CE\uC774 \uC0DD\uAC01\uD574\uB0B4\uB294 \uAC83\uC774\uB2E4."), mdx("h3", null, "\uC0C8\uB85C\uC6B4 \uC601\uC5ED\uC5D0 \uBAB0\uC785\uD558\uB77C."), mdx("p", null, "\uC900\uAC70\uC758 \uD2C0\uC744 \uD655\uC7A5\uD558\uBA74 \uB3C5\uCC3D\uC131\uC774 \uC99D\uAC00\uD55C\uB2E4. \uC0C8\uB85C\uC6B4 \uAE30\uC220\uC774\uB098 \uAE30\uB2A5\uC744 \uBC30\uC6B0\uB294 \uAC83\uC774 \uD55C \uAC00\uC9C0 \uBC29\uBC95\uC774\uB2E4. \uC21C\uD658\uADFC\uBB34, \uC0C8\uB85C\uC6B4 \uBB38\uD654\uC5D0 \uB300\uD574 \uBC30\uC6B0\uB294 \uBC29\uBC95 \uB4F1\uC774 \uC788\uB2E4."), mdx("h3", null, "\uD560 \uC77C\uC744 \uC804\uB7B5\uC801\uC73C\uB85C \uBBF8\uB8E8\uB77C."), mdx("p", null, "\uC0C8 \uC544\uC774\uB514\uC5B4\uB97C \uC0DD\uAC01\uD574\uB0B4\uB824\uACE0 \uD560 \uB54C\uB294 \uC544\uC774\uB514\uC5B4\uAC00 \uC644\uC131\uB418\uAE30 \uC804\uC5D0 \uC77C\uBD80\uB7EC \uC0DD\uAC01\uC744 \uBA48\uCDB0\uBCF4\uB77C. \uC911\uAC04\uC5D0 \uBA48\uCD94\uACE0 \uD734\uC2DD\uC744 \uCDE8\uD558\uBA74, \uB354 \uB2E4\uCC44\uB85C\uC6B4 \uC0AC\uACE0\uB97C \uD558\uAC8C \uB418\uACE0, \uC0C8\uB85C \uC0DD\uAC01\uD574\uB0B8 \uC544\uC774\uB514\uC5B4\uAC00 \uBB34\uB974\uC775\uC744 \uC2DC\uAC04\uC744 \uC8FC\uAC8C \uB41C\uB2E4."), mdx("h3", null, "\uB3D9\uB8CC\uB4E4\uB85C\uBD80\uD130 \uB354 \uB9CE\uC740 \uD53C\uB4DC\uBC31\uC744 \uAD6C\uD558\uB77C."), mdx("p", null, "\uB2F9\uC0AC\uC790\uAC00 \uC790\uC2E0\uC758 \uC544\uC774\uB514\uC5B4\uB97C \uD310\uB2E8\uD558\uB294 \uC77C\uC740 \uC5B4\uB835\uB2E4. \uAC00\uC7A5 \uC815\uD655\uD55C \uD3C9\uAC00\uB97C \uC5BB\uAE30 \uC704\uD574\uC11C\uB