  }), mdx("h1", null, "\uBA38\uB2C8 - \uB86D \uBB34\uC5B4"), mdx("p", null, "\u2018\uB808\uBC84\uB9AC\uC9C0\u2019 \uCC45\uC744 \uC77D\uACE0 \uC800\uC790\uC758 \uB2E4\uB978 \uCC45\uC774 \uC77D\uACE0 \uC2F6\uC5B4 \u2018\uBA38\uB2C8\u2019\uB77C\uB294 \uCC45\uC744 \uC120\uD0DD\uD588\uB2E4. \u2018\uB808\uBC84\uB9AC\uC9C0\u2019 \uC774\uD6C4\uC5D0 \uBC1C\uAC04\uB41C \uCC45\uC778\uB370, \uCC45\uC744 \uC77D\uB2E4\uBCF4\uBA74 \u2018\uB808\uBC84\uB9AC\uC9C0\u2019\uC758 \uB0B4\uC6A9\uC774 \uB9CE\uC774 \uD3EC\uD568\uB418\uC5B4 \uC788\uB2E4."), mdx("p", null, "\uC55E \uBD80\uBD84\uC5D0\uC11C\uB294 \uBD80\uC5D0 \uB300\uD55C \uAD00\uC810\uC744 \uBC14\uB77C\uBCF4\uB294 \uBC95\uC5D0 \uB300\uD574 \uC124\uBA85\uD55C\uB2E4. \uC9C4\uC815\uD55C \uBD80\uC5D0 \uB300\uD574 \uC54C\uACE0, \uB3C8\uC758 \uC131\uACA9\uACFC \uC758\uBBF8\uC5D0 \uB300\uD574 \uB2E4\uB978 \uC2DC\uAC01\uC73C\uB85C \uBCFC \uC218 \uC788\uB3C4\uB85D \uD55C\uB2E4. \uB3C8\uC73C\uB85C \uD589\uBCF5\uC744 \uC0B4 \uC218 \uC788\uACE0, \uB3C8\uBC8C\uC774 \uC790\uCCB4\uB294 \uD559\uC2B5\uB418\uB294 \uC2DC\uC2A4\uD15C\uC774\uAE30 \uB54C\uBB38\uC5D0 \uB204\uAD6C\uB77C\uB3C4 \uBD80\uB97C \uC774\uB8F0 \uC218 \uC788\uB2E4. \uBD80 \uC790\uCCB4\uAC00 \uB098\uC05C \uAC83\uC740 \uC544\uB2C8\uB2E4. \uB3C8\uC5D0 \uB300\uD55C \uBD80\uC815\uC801\uC778 \uC0DD\uAC01\uC744 \uBC84\uB824\uC57C \uD55C\uB2E4. \uBD80\uC790\uC5D0 \uB300\uD55C \uB098\uC05C \uC2DC\uC120\uC744 \uAC00\uC9C8 \uD544\uC694\uAC00 \uC5C6\uB2E4."), mdx("p", null, "\uC800\uCD95\uB9CC\uC73C\uB85C\uB294 \uBD80\uC790\uAC00 \uB420 \uC218 \uC5C6\uB2E4\uB294 \uC0AC\uC2E4\uC744 \uC778\uC9C0\uD558\uACE0, \uB3C8\uC744 \uC9C0\uBC30\uD558\uB294 \uC0AC\uB78C\uC774 \uB418\uC5B4\uC57C \uD55C\uB2E4\uACE0 \uAC15\uC870\uD55C\uB2E4. \uADF8 \uC911 \uC778\uC0C1 \uAE4A\uC740 \uAC83\uC740 \u2018\uBD80\uC790\uC758 \uBBFF\uC74C\uACFC \uAC00\uB09C\uD55C \uC0AC\uB78C\uC758 \uBBFF\uC74C\u2019\uC774\uB77C\uB294 \uBD80\uBD84\uC774\uB2E4. \uBD80\uC790\uB4E4\uC758 \uBBFF\uC74C\uC740 \uC804\uCCB4\uC801\uC73C\uB85C \uC0B6\uC744 \uAE0D\uC815\uC801\uC73C\uB85C \uBC14\uB77C\uBCF8\uB2E4. \uB3C8\uC740 \uBAA8\uB4E0 \uC120\uC758 \uADFC\uC6D0\uC774\uBA70, \uC544\uC774\uB514\uC5B4, \uC5D0\uB108\uC9C0, \uC11C\uBE44\uC2A4\uAC00 \uB3C8\uC744 \uBC8C \uC218 \uC788\uAC8C \uD55C\uB2E4. \uC88B\uC740 \uBD80\uCC44\uB294 \uC88B\uC740 \uAC83\uC774\uBA70, \uB3C8\uC774 \uC5F4\uC2EC\uD788 \uC77C\uD558\uAC8C \uB9CC\uB4E4\uC5B4\uC57C \uD55C\uB2E4\uB294 \uAC83. \uADF8\uB9AC\uACE0 \uAC00\uCE58\uAC00 \uB0AE\uC740 \uC77C\uC740 \uD558\uC9C0 \uC54A\uC73C\uBA70, \uB098 \uC790\uC2E0\uC744 \uBBFF\uACE0, \uAC00\uCE58 \uC788\uB294 \uC77C\uC744 \uD558\uBA70, \uB3C8\uC744 \uBC8C\uACE0 \uB098\uB204\uB294 \uAC83\uC774 \uC18C\uBA85\uC774\uB77C \uC0DD\uAC01\uD55C\uB2E4. \uB0B4\uAC00 \uAC00\uC84C\uB358 \uBA87\uBA87 \uC0DD\uAC01\uB4E4\uC774 \uAC00\uB09C\uD55C \uC0AC\uB78C\uB4E4\uC758 \uBBFF\uC74C \uC911\uC5D0 \uC18D\uD574\uC838 \uC788\uC5C8\uB2E4. \uCC45\uC744 \uC77D\uB2E4\uBCF4\uB2C8, \uBD80\uC790\uB4E4\uC774 \uBD80\uC5D0 \uAD00\uD55C \uC0DD\uAC01\uC774 \uB0B4 \uC18D\uC5D0 \uC2A4\uBA70\uB4DC\uB294 \uAC83 \uAC19\uC558\uB2E4."), mdx("p", null, "\uCC45\uC758 \uC911\uBC18\uBD80 \uC774\uD6C4\uBD80\uD130\uB294 \u2018\uB808\uBC84\uB9AC\uC9C0\u2019\uC5D0\uC11C \uB098\uC654\uB358 \uB0B4\uC6A9\uB4E4\uC774 \uC774\uC5B4\uC838 \uB098\uC654\uB2E4. \uBD80\uB97C \uB04C\uC5B4\uC624\uAE30 \uC704\uD574 \u2018\uBE44\uC804\u2019, \u2018\uAC00\uCE58\u2019, \u2018\uD575\uC2EC \uACB0\uACFC \uC601\uC5ED\u2019, \u2018\uC18C\uB4DD \uCC3D\uCD9C \uC5C5\uBB34\u2019, \u2018\uD575\uC2EC \uC131\uACFC \uC9C0\uD45C\u2019\uB97C \uBD84\uC11D\uD574\uBD10\uC57C \uD55C\uB2E4. \uBD80\uB294 \uACF5\uC815\uD55C \uAC00\uCE58 \uAD50\uD658\uACFC \uB808\uBC84\uB9AC\uC9C0\uC758 \uACF1\uC774\uB2E4. \uB864 \uBAA8\uB378\uC758 \uC911\uC694\uC131\uACFC \uB124\uD2B8\uC6CC\uD06C\uC758 \uD798\uB3C4 \uC911\uC694\uD558\uBA70, \uC2E0\uB8B0\uB97C \uC904 \uC218 \uC788\uB294 \uC0AC\uB78C\uC774 \uB418\uC5B4\uC57C \uD55C\uB2E4. \uCD5C\uACE0 \uC790\uC0B0\uC740 \uB098 \uC790\uC2E0\uC774\uAE30 \uB54C\uBB38\uC5D0, \uC2A4\uC2A4\uB85C\uC5D0 \uB300\uD574 \uC54C\uBA70, \uB9CE\uC740 \uACBD\uD5D8\uC744 \uD558\uACE0, \uC2E0\uCCB4\uC801, \uC815\uC2E0\uC801 \uAC74\uAC15\uC744 \uC704\uD574 \uD798\uC368\uC57C \uD55C\uB2E4."), mdx("p", null, "\uB9E4\uC77C \uB9E4\uC77C \uC131\uC7A5\uD558\uAE30 \uC704\uD574 \uCC45\uC744 \uC77D\uACE0, \uC2DC\uAC04\uC744 \uB0B4\uC11C \uACF5\uBD80\uB97C \uD55C\uB2E4. \uC138\uC0C1\uC5D0 \uB3C4\uC6C0\uC774 \uB420 \uC218 \uC788\uB294 \uC11C\uBE44\uC2A4\uB97C \uB9CC\uB4E4\uACE0 \uC788\uB2E4. \uCC45\uC744 \uC77D\uC73C\uBA74 \uC77D\uC744 \uC218\uB85D, \uBD80\uC790\uB4E4\uC758 \uC0DD\uAC01, \uC131\uACF5\uD55C \uC0AC\uB78C\uB4E4\uC758 \uB9C8\uC74C\uAC00\uC9D0\uC774 \uBE44\uC2B7\uD55C \uC720\uD615\uC744 \uB744\uACE0 \uC788\uB2E4\uB294 \uAC83\uC744 \uC54C\uAC8C \uB41C\uB2E4. \uCD5C\uADFC\uC5D0 \uAE0D\uC815\uC801\uC778 \uC2B5\uAD00\uB4E4\uC744 \uD558\uB098\uC529 \uD558\uB098\uC529 \uB098\uC5D0\uAC8C \uC801\uC6A9\uD558\uACE0 \uC788\uB2E4. \uADF8\uB798\uC11C \uC7AC\uBC0C\uB2E4. \uD765\uBBF8\uB86D\uB2E4. \uC0B6\uC774 \uC810\uC810 \uC990\uAC81\uB2E4. \uC870\uAE08\uC529 \uC131\uC815\uD558\uACE0 \uC788\uB294 \uB098 \uC790\uC2E0\uC744 \uBCF4\uAE30 \uB300\uBB38\uC774\uB2E4."), mdx("p", null, "\uB2E4\uB978 \uC0AC\uB78C\uB4E4\uC774 \uBCF4\uBA74 \uD604\uC7AC \uB098\uC758 \uC0C1\uD669\uC740 \uB9E4\uC6B0 \uC88B\uC9C0 \uC54A\uC740 \uAC83 \uAC19\uC774 \uBCF4\uC77C \uAC83\uC774\uB2E4. \uD558\uC9C0\uB9CC \uC774 \uAC19\uC740 \uC0C1\uD669\uC5D0\uC11C\uB3C4 \uC990\uAC81\uAC8C \uC77C\uD558\uACE0 \uC788\uB2E4. \uBD80\uC815\uC801\uC778 \uC0DD\uAC01\uB4E4\uC740 \uD558\uC9C0 \uC54A\uB294\uB2E4. \uB9CE\uC740 \uAC83\uB4E4\uC744 \uC783\uC5B4\uBC84\uB824\uC11C \uC624\uD788\uB824 \uC790\uC720\uB86D\uB2E4. \uB098\uC5D0\uAC8C \uC911\uC694\uD55C \uAC83\uACFC \uC911\uC694\uD558\uC9C0 \uC54A\uC740 \uAC83\uB4E4\uC744 \uAD6C\uBD84\uD558\uAC8C \uB418\uC5C8\uB2E4. \uD798\uB4E0 \uC0C1\uD669\uC774\uB77C \uB354 \uBC30\uC6B8 \uAC83\uC774 \uB9CE\uB2E4. \uAFB8\uC900\uD788, \uAFB8\uC900\uD788 \uBC30\uC6B0\uBA74\uC11C \uC131\uC7A5\uD574 \uAC08 \uAC83\uC774\uB2E4. \uADF8\uB9AC\uACE0 \uAF2D \uC131\uACF5\uD560 \uAC83\uC774\uB2E4."), mdx("hr", null), mdx("h1", null, "\uAE30\uC5B5\uC5D0 \uB0A8\uB294 \uAE00\uB4E4"), mdx("p", null, "\uB354 \uB9CE\uC774, \uB354 \uBE68\uB9AC, \uB354 \uB611\uB611\uD558\uAC8C \uBD80\uB97C \uB9CC\uB4DC\uB294 \uACF5\uC2DD, \u201C\uB3C8\uACFC \uD589\uBCF5\uC774 \uBCC4\uAC1C\uB77C\uB294 \uC0DD\uAC01\uC740 \uD2C0\uB838\uB2E4!\u201D"), mdx("h1", null, "\uB204\uAD6C\uB098 \uC774\uB8F0 \uC218 \uC788\uB294 \uBD80\uC758 \uBBF8\uB798"), mdx("p", null, "\u201C\uAC00\uB09C\uD558\uAC8C \uD0DC\uC5B4\uB09C \uAC74 \uB2F9\uC2E0 \uC798\uBABB\uC774 \uC544\uB2C8\uC9C0\uB9CC, \uAC00\uB09C\uD558\uAC8C \uC8FD\uB294 \uAC74 \uB2F9\uC2E0 \uC798\uBABB\uC774\uB2E4.\u201D - \uBE4C \uAC8C\uC774\uCE20"), mdx("h2", null, "\uB2F9\uC2E0\uC758 \uC0DD\uAC01\uC774 \uBD80\uB97C \uB9C9\uACE0 \uC788\uB2E4\uBA74?"), mdx("h2", null, "\uBD80\uC790\uB4E4\uC758 \uC138 \uAC00\uC9C0 \uACF5\uD1B5\uC810"), mdx("ol", null, mdx("li", {
