  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx("h1", null, "\uB9C8\uD754\uC5EC\uB35F\uBC88\uC9F8. 2019 \uCD98\uCC9C \uADF8\uB780\uD3F0\uB3C4"), mdx("p", null, "\uC0C8\uBCBD 3\uC2DC \uBC18. \uC544\uCE68 \uC77C\uCC0D\uBD80\uD130 \uC77C\uC5B4\uB098, \uCD98\uCC9C\uC73C\uB85C \uD5A5\uD560 \uC900\uBE44\uB97C \uD588\uB2E4. \uD0DC\uD48D\uC774 \uC62C\uB77C\uC628\uB2E4\uB294\uB370, \uC624\uB298\uC740 \uAD1C\uCC2E\uACA0\uC9C0? \uB2E4\uD589\uC774 \uBE44\uC18C\uC2DD\uC740 \uC5C6\uB2E4. \uC0C1\uD6C8\uC774\uC9D1\uC5D0\uC11C \uB9CC\uB098\uC11C \uCD9C\uBC1C\uD558\uAE30\uB85C \uD588\uAE30 \uB54C\uBB38\uC5D0 \uC790\uC804\uAC70\uB97C \uD0C0\uACE0 \uAC00\uC57C\uD588\uB2E4. \uD574\uAC00 \uB728\uAE30 \uC804\uC774\uB77C \uBC16\uC740 \uC5B4\uB450\uC6E0\uB2E4. \uC624\uB7AB\uB9CC\uC5D0 \uC804\uC870\uB4F1, \uD6C4\uBBF8\uB4F1\uC744 \uC7A5\uCC29\uD588\uB2E4. 30\uBD84 \uC815\uB3C4\uC758 \uC9E7\uC740 \uAC70\uB9AC\uC600\uAE30\uC5D0 \uBD80\uB2F4\uC774 \uC5C6\uB2E4. \uB0B4\uAC00 \uC790\uC804\uAC70\uB97C \uCC98\uC74C \uC0C0\uC744 \uB54C \uBC1B\uC558\uB358 \uAE5C\uBE61\uC774 \uD6C4\uBBF8\uB4F1\uC774 \uBCF4\uC600\uB2E4. \uC624\uB798 \uB418\uC5C8\uB294\uB300\uB3C4 \uC798 \uB3D9\uC791\uD558\uB124? \uC0C1\uD6C8\uC774 \uC9D1\uC5D0 \uB3C4\uCC29\uD588\uC744 \uB54C \uBCF4\uB2C8, \uBD88\uC774 \uD76C\uBBF8\uD588\uB2E4. \uB108\uB3C4 \uB2E4\uB418\uC5C8\uAD6C\uB098. \uB124\uC774\uBC84 \uC9C0\uB3C4\uB85C \uCD5C\uB2E8\uAC70\uB9AC\uB85C \uAC00\uB2C8, \uC0DD\uAC01\uBCF4\uB2E4 \uAE08\uBC29 \uB3C4\uCC29\uD588\uB2E4. \uC774\uB978 \uC0C8\uBCBD\uC774\uB77C \uCC28\uB3C4 \uC5C6\uC5B4 \uC88B\uC558\uB2E4."), mdx("p", null, "\uC740\uD638\uC640 \uB3D9\uC2DC\uC5D0 \uB3C4\uCC29\uD588\uB2E4. \uC790\uC804\uAC70\uB97C \uC2E4\uC5C8\uB2E4. \uC774\uC81C \uBA87 \uBC88 \uAC19\uC774 \uB77C\uC774\uB529\uC744 \uAC00\uB2E4\uBCF4\uB2C8 \uC790\uC804\uAC70\uB97C \uC2E3\uB294 \uBC29\uBC95\uB3C4 \uC775\uC219\uD574\uC838 \uC18D\uB3C4\uB3C4 \uBE68\uB77C\uC84C\uB2E4. \uB2E4\uB4E4 \uB9D0\uD558\uC9C0 \uC54A\uC544\uB3C4 \uC790\uC5F0\uC2A4\uB7FD\uAC8C \uC694\uB9AC\uC870\uB9AC \uB9DE\uCDB0\uC11C \uC138\uD305\uD55C\uB2E4. \uCD98\uCC9C\uC73C\uB85C \uD5A5\uD558\uB294 \uAE38\uC5D0\uB294 \uC740\uC2EC\uC774 \uB204\uB098\uAC00 \uC2F8\uC8FC\uC2E0 \uACFC\uC77C \uB3C4\uC2DC\uB77D\uC744 \uBA39\uC5C8\uB2E4. \uD3C9\uC18C \uACFC\uC77C\uC744 \uC798 \uCC59\uACA8\uBA39\uC9C0 \uC54A\uAE30\uC5D0, \uC774\uB7F4 \uB54C\uB9CC \uBA39\uC744 \uC218 \uC788\uB2E4. \uACFC\uC77C\uC740 \uC5ED\uC2DC \uB9DB\uC788\uB2E4. \uC11C\uC6B8\uD300\uC740 2\uC2DC\uAC04 \uB0A8\uC9D3 \uAC78\uB824 \uCD98\uCC9C\uC5D0 \uB3C4\uCC29\uD588\uB2E4. \uAE40\uD574\uD300\uC740 \uC5B4\uC81C \uBC24\uC5D0 \uBA3C\uC800 \uC640\uC11C \uC790\uACE0 \uC788\uC5C8\uB2E4. 4~5\uC2DC\uAC04 \uAC78\uB824\uC11C \uAE40\uD574\uC5D0\uC11C \uCD98\uCC9C\uAE4C\uC9C0 \uC62C\uB77C\uC624\uB2E4\uB2C8, \uB300\uB2E8\uD558\uB2E4. \uB09C \uC544\uB9C8 \uBD80\uC0B0\uC5D0 \uC788\uC5C8\uB2E4\uBA74, \uB108\uBB34 \uBA40\uC5B4\uC11C \uB300\uD68C\uC5D0 \uCC38\uAC00 \uC548\uD588\uC744 \uAC83 \uAC19\uC740\uB370\u2026"), mdx("p", null, "\uC5B4\uC81C \uC810\uC2EC\uB54C \uBD80\uD130 \uC18D\uC774 \uC548\uC88B\uC558\uB2E4. \uCCB4\uD55C \uAC83 \uAC19\uC740 \uB290\uB08C. \uADF8\uB798\uB3C4 \uC544\uCE68\uC5D0 \uBB50\uB77C\uB3C4 \uBA39\uACE0 \uAC00\uC57C\uD560 \uAC83 \uAC19\uC544\uC11C, \uC544\uCE68\uC744 \uAC04\uB2E8\uD788 \uBA39\uC5C8\uB2E4. \uCD98\uCC9C\uC5D0 \uB3C4\uCC29\uD558\uBA74\uC11C \uD55C\uC815\uC2DD \uC9D1\uC5D0 \uAC00\uC11C \uBC25\uB3C4 \uCC59\uACA8 \uBA39\uC5C8\uB2E4. \uC624\uB7AB\uB9CC\uC5D0 \uC2DC\uACE8 \uBC25\uC0C1 \uAC19\uC740 \uC544\uCE68\uC744 \uBA39\uC73C\uB2C8 \uB4E0\uB4E0\uD558\uB2E4."), mdx("p", null, "\uCD98\uCC9C \uADF8\uB780\uD3F0\uB3C4 \uC2DC\uC791 \uC9C0\uC810\uC778 \uC1A1\uC554 \uB808\uD3EC\uCE20 \uD0C0\uC6B4\uC5D0 \uB3C4\uCC29\uD588\uB2E4. \uBC43\uC18D\uC5D0\uC11C \uC2E0\uD638\uAC00 \uC654\uB2E4. \uBB54\uAC00 \uBD88\uC548\uD55C \uB290\uB08C\uC774\uB2E4. \uC11C\uB458\uB7EC \uD654\uC7A5\uC2E4\uB85C \uD5A5\uD588\uB2E4. \uADFC\uCC98\uC5D0 \uC8FC\uACBD\uAE30\uC7A5\uC5D0 \uBB38\uC774 \uB2E4 \uB2EB\uD600 \uC788\uC5B4\uC11C, \uAE38\uC744 \uBB3C\uC5B4\uBB3C\uC5B4 X\uAC8C\uC784\uD30C\uD06C(?)\uC5D0 \uC788\uB294 \uD654\uC7A5\uC2E4\uB85C \uD5A5\uD588\uB2E4. \uADFC\uB370 \uC0AC\uB78C\uB4E4\uC758 \uC904\uC774 \uB108\uBB34 \uAE38\uB2E4. 5\uBD84\uB0A8\uC9D3 \uAE30\uB2E4\uB824\uBCF4\uB2E4\uAC00, \uB354\uC774\uC0C1 \uC5EC\uAE30 \uC788\uC73C\uBA74 \uC548\uB420 \uAC83 \uAC19\uB2E4\uB294 \uB290\uB08C\uC774 \uB4E4\uC5C8\uB2E4. \uB124\uC774\uBC84 \uC9C0\uB3C4\uB97C \uCF1C\uC11C \uD654\uC7A5\uC2E4\uC744 \uAC80\uC0C9\uD574\uBCF4\uB2C8 \uBE59\uC0C1\uACBD\uAE30\uC7A5 \uC606\uC5D0 \uD654\uC7A5\uC2E4\uC774 \uC788\uC5C8\uB2E4. \uAC08\uAE4C\uB9D0\uAE4C 5\uBD84\uC815\uB3C4 \uB354 \uACE0\uBBFC\uC744 \uD588\uB2E4. \uADF8\uB798, \uAC00\uC790. \uC5EC\uAE30\uC11C \uAE30\uB2E4\uB9AC\uB2E4\uAC04 \uCD9C\uBC1C\uC2DC\uAC04\uC5D0 \uB9DE\uCD94\uC9C0\uB3C4 \uBABB\uD560 \uAC83 \uAC19\uC558\uB2E4. \uC870\uAE45\uD558\uB4EF \uB6F0\uC5B4\uAC14\uB2E4. \uD06C\uC73D. \uC5EC\uAE30\uB3C4 \uC8FC\uCC28\uC7A5\uC774 \uC788\uC5B4 \uADF8\uB7F0\uC9C0, \uC0AC\uB78C\uB4E4\uC774 \uC8FC\uCC28\uD558\uACE0 \uD654\uC7A5\uC2E4\uC744 \uB9CE\uC774 \uAC00\uB294 \uBAA8\uC591\uC0C8\uB2E4. \uC5EC\uAE30\uB3C4 \uC904\uC774 \uD654\uC7A5\uC2E4 \uD1B5\uB85C \uBC14\uAE65\uAE4C\uC9C0 \uC774\uC5B4\uC838 \uC788\uC5C8\uB2E4. \uBC14\uB85C \uB2E4\uC2DC \uB098\uC654\uB2E4. \uD639\uC2DC\uB098 \uB2E4\uB978 \uACBD\uAE30\uC7A5\uC5D0 \uC788\uC9C0 \uC54A\uC744\uAE4C \uD574\uC11C \uC871\uAD6C\uC7A5\uAE4C\uC9C0 \uAC14\uB2E4. \uAC70\uAE34 \uC544\uC608 \uBB38\uC744 \uC548\uC5F4\uC5C8\uB2E4. \uC800\uAE30 \uBA40\uB9AC \uD074\uB77C\uC774\uBC0D\uD558\uB294 \uACF3\uC774 \uBCF4\uC778\uB2E4. \uADFC\uCC98\uC5D0 \uC788\uB294 \uBD84\uAED8 \uBB3C\uC5C8\uB354\uB2C8 \uC800\uAE30 \uAC00\uBA74 \uC788\uC744 \uAC83\uC774\uB77C \uD588\uB2E4. \uAD7F. \uBA40\uB9AC\uC788\uC5B4\uC11C \uC548\uBCF4\uC600\uB294\uB370, \uB05D\uAE4C\uC9C0 \uAC00\uBCF4\uB2C8 1\uC778\uC6A9 \uD654\uC7A5\uC2E4\uC774 \uC788\uC5C8\uB2E4. \uD6C4\uB2E4\uB2E5 \uB2EC\uB824\uAC14\uB2E4. \uC544\uBB34\uB3C4 \uC5C6\uC5B4\uB77C. \uD734\uC6B0. \uB2E4\uD589\uC774 \uC544\uBB34\uB3C4 \uC5C6\uB2E4. \uC774\uB7F0 \uD589\uC6B4\uC774. \uC624\uB7AB\uB9CC\uC5D0 \uB290\uAEF4\uBCF4\uB294 \uACE0\uD1B5\uACFC \uD589\uC6B4\uC774\uC5C8\uB2E4. \uB355\uBD84\uC5D0 \uB6F0\uC5B4\uB2E4\uB2CC\uB2E4\uACE0 \uC6CC\uBC0D\uC5C5\uC740 \uB41C \uAC83 \uAC19\uB2E4. \uCD9C\uBC1C \uC2DC\uAC04\uC774 \uC5BC\uB9C8 \uB0A8\uC9C0 \uC54A\uC558\uB2E4. \uB3CC\uC544\uAC00\uB294 \uAE38\uB3C4 \uC11C\uB458\uB7EC \uB6F0\uC5B4\uAC14\uB2E4. \uC5BC\uB9C8\uB9CC\uC5D0 \uB6F0\uC5B4\uBCF8 \uAC74\uC9C0 \uC6D0."), mdx("p", null, "\uCC28\uC5D0 \uB3C4\uCC29\uD558\uC790\uB9C8\uC790, \uC790\uC804\uAC70\uB97C \uC138\uD305\uD588\uB2E4. \uB2E4\uD589\uC774\uB3C4 \uCE5C\uAD6C\uB4E4\uC774 \uBC30\uBC88\uC744 \uBC1B\uC544\uB2E4\uC92C\uC11C, \uCD9C\uBC1C \uC2DC\uAC04\uC5D4 \uB2A6\uC9C0 \uC54A\uACA0\uB2E4. \uC0C1\uD6C8\uC774\uB294 \uB290\uAE0B\uD558\uAC8C \uADFC\uCC98\uC5D0 \uC788\uB294 \uD654\uC7A5\uC2E4\uC744 \uCC3E\uC558\uACE0, \uC5EC\uC720\uB86D\uAC8C \uAC14\uB2E4\uC654\uB2E4\uBA70, \uC584\uBC09\uAC8C \uB9D0\uD588\uB2E4. \uB09C \uD63C\uC790\uC11C \uC329\uACE0\uC0DD\uC744 \uD588\uB294\uB370\u2026 \uD654\uC7A5\uC2E4\uC740 \uCF54\uC55E\uC5D0 \uC788\uC5C8\uB2E4\uACE0 \uD55C\uB2E4. \uB0B4 \uB9C8\uC74C\uC774 \uAE09\uD558\uB2C8, \uBC14\uB85C \uADFC\uCC98\uC758 \uAC83\uB3C4 \uBCF4\uC9C0 \uBABB\uD588\uB358 \uAC83 \uAC19\uB2E4."), mdx("p", null, "\uC18D\uC744 \uBE44\uC6B0\uB2C8, \uBAB8\uC774 \uAC00\uBCBC\uC6CC\uC84C\uB2E4. \uB9C8\uC74C\uAE4C\uC9C0 \uAC00\uBCBC\uC6CC\uC838 \uC8FC\uBCC0 \uC0AC\uB78C\uB4E4\uC774 \uBCF4\uC774\uAE30 \uC2DC\uC791\uD55C\uB2E4. \uAE40\uD574\uD300\uC740 \uC55E\uCABD\uC5D0\uC11C \uBA3C\uC800 \uCD9C\uBC1C\uD588\uACE0, \uC11C\uC6B8\uD300\uC740 \uB4A4\uCABD\uC5D0\uC11C \uCD9C\uBC1C\uD588\uB2E4. \uC0AC\uB78C\uB4E4\uC774 \uB9CE\uC73C\uB2C8 \uD798\uC774 \uB09C\uB2E4. \uC5ED\uC2DC \uB77C\uC774\uB529\uC740 \uD568\uAED8 \uD560 \uB54C \uC7AC\uBBF8\uAC00 10\uBC30\uAC00 \uB41C\uB2E4. \uC6B0\uB974\uB974 \uBAB0\uB824 \uAC00\uB2E4\uBCF4\uB2C8 \uC18D\uB3C4\uB3C4 \uAF64\uB098 \uBE60\uB974\uB2E4. \uAE40\uD574\uD300\uACFC \uB9CC\uB098\uC57C \uD558\uB2C8, \uC0C1\uD6C8\uC774\uAC00 \uC11C\uB458\uB800\uB2E4. \uCCAB \uC5C5\uD790 \uAD6C\uAC04\uC5D0\uC11C \uAE40\uD574\uD300\uACFC \uB9CC\uB0AC\uB2E4. \uC624\uB8E8\uB9C9\uD300 \uC644\uC131. \uB2E4\uAC19\uC774 \uAC00\uB2C8 \uD798\uC774 \uB354\uC6B1 \uB09C\uB2E4. \uC774\uBC88 \uB300\uD68C\uC5D0\uC11C \uC0C1\uD6C8\uC774, \uCDA9\uC6D0\uC774\uB97C \uAE30\uD544\uCF54 \uB530\uB77C\uAC00\uACA0\uB178\uB77C\uACE0 \uBAA9\uD45C\uB97C \uC138\uC6E0\uAE30 \uB54C\uBB38\uC5D0 \uCD5C\uB300\uD55C \uBD99\uC5B4\uAC00\uB824\uACE0 \uD588\uB2E4. \uADF8\uB798\uC11C \uADF8\uB7F0\uC9C0, \uC624\uBC84\uD398\uC774\uC2A4\uB97C \uD55C \uAC83 \uAC19\uAE30\uB3C4 \uD558\uB2E4. \uBC8C\uC5B4\uC9C0\uC9C0 \uC54A\uC73C\uB824\uACE0 \uD798\uC744 \uB9CE\uC774 \uC368\uC11C \uB2EC\uB838\uB2E4. \uB450\uBC88\uC9F8 \uC5C5\uD790 \uC774\uD6C4, \uB0B4\uB9AC\uB9C9\uC744 \uAC00\uB2E4\uBCF4\uB2C8 \uC624\uB8E8\uB9C9\uD300\uACFC \uBC8C\uC5B4\uC84C\uB2E4. \uCDA9\uC6D0\uC774\uB294 di2(?)\uB77C\uB294 \uAC83\uC774 \uBC29\uC804\uB418\uC11C \uAE30\uC544 \uBCC0\uC18D\uC774 \uC548\uB41C\uB2E8\uB2E4. \uADF8\uB7F0\uB370\uB3C4 \uAC81\uB098\uAC8C \uBE68\uB9AC \uD398\uB51C\uB9C1\uC744 \uAD74\uB824\uC11C \uC798 \uB530\uB77C\uC628\uB2E4. \uB0B4\uB9AC\uB9C9 \uC774\uD6C4 \uBA3C\uC800 \uAC04 \uCE5C\uAD6C\uB4E4\uC744 \uB530\uB77C\uC7A1\uC9C0 \uBABB\uD558\uACE0, \uCDA9\uC6D0\uC774\uC640 \uB098\uB791 \uB5A8\uC5B4\uC84C\uB2E4. \uB098\uB77C\uB3C4 \uB530\uB77C \uAC00\uC57C\uACA0\uB2E4 \uC2F6\uC5B4 \uBC84\uC2A4\uC5D0 \uD0D1\uC2B9\uD558\uB824\uACE0 \uC314\uAC8C \uB2EC\uB838\uB2E4. \uBC84\uC2A4\uB97C \uC774\uB9AC\uC800\uB9AC \uAC08\uC544\uD0C0\uBA70 \uAC00\uB2E4\uBCF4\uB2C8 \uAE09\uD589 \uBC84\uC2A4\uB97C \uD0D4\uB2E4. \uD55C\uCC38\uC744 \uB2EC\uB9AC\uB2E4\uBCF4\uB2C8, \uC800~~~ \uBA40\uB9AC\uC11C \uC0C1\uD6C8, \uC6B0\uC900, \uC740\uD638\uAC00 \uAC00\uACE0 \uC788\uB294 \uAC83\uC744 \uBC1C\uACAC\uD588\uB2E4. \uC774 \uC18D\uB3C4\uB85C \uAC00\uBA74 \uC548\uB418\uACA0\uB2E4 \uC2F6\uC